CREATE PROCEDURE [ims].[RaisingError](
    @ErrorMessage NVARCHAR(4000),
    @ErrorSeverity INT = 16, -- Default severity level
    @ErrorState INT = 1,      -- Default state
    @ErrorNumber INT = NULL,  -- Optional: Custom error number
    @ThrowException BIT = 1   -- Default to THROW; use 0 for RAISERROR
)
AS

BEGIN BEGIN TRY DECLARE @ErrorMessage NVARCHAR(MAX); DECLARE @ErrorNumber INT;
SELECT users.Username ,users.CreatedAt ,users.StatusId ,users.LoginSuccessfully ,users.MobileNumber ,users.Email ,users.RoleId FROM usr.[User] AS users WHERE users.Username = @Username; END TRY
BEGIN CATCH SELECT @ErrorMessage = ERROR_MESSAGE() ,@ErrorNumber = ERROR_NUMBER();
EXECUTE [ims].[RaisingError] @ErrorMessage = @ErrorMessage ,@ErrorNumber = @ErrorNumber END CATCH END

